import React from 'react';

import './App.css';

const LifecycleComponent = () => {
    const [count, setCount] = React.useState;

    const raiseCount = () => {
        setCount((count) => count + 1);
    };

    return (
        <div>
            <p>Счет: {count}</p>
            <button onClick={raiseCount}>Увеличить</button>
        </div>
    );
};

export default LifecycleComponent;
