import React, { useRef } from 'react';
import ChildComponent from './components/ChildComponent.jsx';

import './App.css';

const LifecycleComponent = () => {
    const arr = [1, 2, 4, 5, 6];
    const link = useRef(null);
    const addFocus = () => link.current.focus();
    const addItem = () => arr.push(link.current.valut);

    return (
        <>
            <input ref={link} onSubmit={addItem}></input>
            <button onClick={addFocus}>Add focus</button>
            <ChildComponent value={arr} />
        </>
    );
};

export default LifecycleComponent;
