import React from 'react';

export default function List(props) {
    const listItems = props.value;
    const textInput = React.useRef(null);
    const addValue = () => console.log(textInput.current.value);

    const finalItems = listItems.map((item) => (
        <li ref={textInput} key={item.toString()}>
            <button onClick={addValue}>Add !!!</button>
            {item}
        </li>
    ));

    return <ul>{finalItems}</ul>;
}
